	(1701, 'question1.mp3', 'D', 'question1.png', 1, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) A woman is standing near a desk.\n\n(B) A woman is climbing some stairs.\n\n(C) A woman is entering a building.\n\n(D) A woman is looking at a map.', 18),
	(1702, 'question2.mp3', 'C', 'question2.png', 2, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) She’s swimming in the water.\n\n(B) She’s jogging near the ocean.\n\n(C) Chairs are set up on the beach.\n\n(D) A hat is lying on the sand.', 18),
	(1703, 'question3.mp3', 'B', 'question3.png', 3, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) She’s reaching for an item from a display case.\n\n(B) She’s holding on to a shopping cart.\n\n(C) Some goods are being arranged on shelves.\n\n(D) Some merchandise has fallen on the floor.', 18),
	(1704, 'question4.mp3', 'D', 'question4.png', 4, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) A fence is being painted in a park.\n\n(B) One of the women is putting on a jacket.\n\n(C) Some people are working in a garden.\n\n(D) Some people are looking up at the trees.', 18),
	(1705, 'question5.mp3', 'A', 'question5.png', 5, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) A carpet is being rolled up.\n\n(B) Some curtains have been closed.\n\n(C) Some cushions are piled on the floor.\n\n(D) Some reading materials have been placed in front of a sofa.', 18),
	(1706, 'question6.mp3', 'B', 'question6.png', 6, 'A', 'B', 'C', 'D', NULL, 1, NULL, '(A) Some musicians are performing on a porch.\n\n(B) Some steps are being repaired.\n\n(C) Some music stands are being folded up.\n\n(D) Some instruments have been placed in their cases.', 18),
	(1707, 'question7.mp3', 'A', NULL, 7, 'A', 'B', 'C', NULL, NULL, 2, NULL, 'When should we leave for the airport?\n\n(A) This afternoon at two o’clock.\n\n(B) I prefer an aisle seat.\n\n(C) Mr. Choi traveled there last year.', 18),
